bool Announcements::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("text"))
    {
        if(!validJsonOfField(1, "text", pJson["text"], err, true))
            return false;
    }
    if(pJson.isMember("show_until"))
    {
        if(!validJsonOfField(2, "show_until", pJson["show_until"], err, true))
            return false;
    }
    if(pJson.isMember("active"))
    {
        if(!validJsonOfField(3, "active", pJson["active"], err, true))
            return false;
    }
    if(pJson.isMember("created_at"))
    {
        if(!validJsonOfField(4, "created_at", pJson["created_at"], err, true))
            return false;
    }
    if(pJson.isMember("updated_at"))
    {
        if(!validJsonOfField(5, "updated_at", pJson["updated_at"], err, true))
            return false;
    }
    return true;
}
