bool Changesets::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("repository_id"))
    {
        if(!validJsonOfField(1, "repository_id", pJson["repository_id"], err, true))
            return false;
    }
    else
    {
        err="The repository_id column cannot be null";
        return false;
    }
    if(pJson.isMember("revision"))
    {
        if(!validJsonOfField(2, "revision", pJson["revision"], err, true))
            return false;
    }
    else
    {
        err="The revision column cannot be null";
        return false;
    }
    if(pJson.isMember("committer"))
    {
        if(!validJsonOfField(3, "committer", pJson["committer"], err, true))
            return false;
    }
    if(pJson.isMember("committed_on"))
    {
        if(!validJsonOfField(4, "committed_on", pJson["committed_on"], err, true))
            return false;
    }
    else
    {
        err="The committed_on column cannot be null";
        return false;
    }
    if(pJson.isMember("comments"))
    {
        if(!validJsonOfField(5, "comments", pJson["comments"], err, true))
            return false;
    }
    if(pJson.isMember("commit_date"))
    {
        if(!validJsonOfField(6, "commit_date", pJson["commit_date"], err, true))
            return false;
    }
    if(pJson.isMember("scmid"))
    {
        if(!validJsonOfField(7, "scmid", pJson["scmid"], err, true))
            return false;
    }
    if(pJson.isMember("user_id"))
    {
        if(!validJsonOfField(8, "user_id", pJson["user_id"], err, true))
            return false;
    }
    return true;
}
