bool Comments::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("commented_type"))
    {
        if(!validJsonOfField(1, "commented_type", pJson["commented_type"], err, true))
            return false;
    }
    if(pJson.isMember("commented_id"))
    {
        if(!validJsonOfField(2, "commented_id", pJson["commented_id"], err, true))
            return false;
    }
    if(pJson.isMember("author_id"))
    {
        if(!validJsonOfField(3, "author_id", pJson["author_id"], err, true))
            return false;
    }
    if(pJson.isMember("comments"))
    {
        if(!validJsonOfField(4, "comments", pJson["comments"], err, true))
            return false;
    }
    if(pJson.isMember("created_on"))
    {
        if(!validJsonOfField(5, "created_on", pJson["created_on"], err, true))
            return false;
    }
    else
    {
        err="The created_on column cannot be null";
        return false;
    }
    if(pJson.isMember("updated_on"))
    {
        if(!validJsonOfField(6, "updated_on", pJson["updated_on"], err, true))
            return false;
    }
    else
    {
        err="The updated_on column cannot be null";
        return false;
    }
    return true;
}
