bool CustomOptions::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("custom_field_id"))
    {
        if(!validJsonOfField(1, "custom_field_id", pJson["custom_field_id"], err, true))
            return false;
    }
    if(pJson.isMember("position"))
    {
        if(!validJsonOfField(2, "position", pJson["position"], err, true))
            return false;
    }
    if(pJson.isMember("default_value"))
    {
        if(!validJsonOfField(3, "default_value", pJson["default_value"], err, true))
            return false;
    }
    if(pJson.isMember("value"))
    {
        if(!validJsonOfField(4, "value", pJson["value"], err, true))
            return false;
    }
    if(pJson.isMember("created_at"))
    {
        if(!validJsonOfField(5, "created_at", pJson["created_at"], err, true))
            return false;
    }
    if(pJson.isMember("updated_at"))
    {
        if(!validJsonOfField(6, "updated_at", pJson["updated_at"], err, true))
            return false;
    }
    return true;
}
