bool News::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("project_id"))
    {
        if(!validJsonOfField(1, "project_id", pJson["project_id"], err, true))
            return false;
    }
    if(pJson.isMember("title"))
    {
        if(!validJsonOfField(2, "title", pJson["title"], err, true))
            return false;
    }
    if(pJson.isMember("summary"))
    {
        if(!validJsonOfField(3, "summary", pJson["summary"], err, true))
            return false;
    }
    if(pJson.isMember("description"))
    {
        if(!validJsonOfField(4, "description", pJson["description"], err, true))
            return false;
    }
    if(pJson.isMember("author_id"))
    {
        if(!validJsonOfField(5, "author_id", pJson["author_id"], err, true))
            return false;
    }
    if(pJson.isMember("created_on"))
    {
        if(!validJsonOfField(6, "created_on", pJson["created_on"], err, true))
            return false;
    }
    if(pJson.isMember("comments_count"))
    {
        if(!validJsonOfField(7, "comments_count", pJson["comments_count"], err, true))
            return false;
    }
    return true;
}
